// HasNovels applies the HasEdge predicate on the "novels" edge.
func HasNovels() predicate.NovelClassify {
	return predicate.NovelClassify(func(s *sql.Selector) {
		step := sqlgraph.NewStep(
			sqlgraph.From(Table, FieldID),
			sqlgraph.To(NovelsTable, FieldID),
			sqlgraph.Edge(sqlgraph.O2M, false, NovelsTable, NovelsColumn),
		)
		sqlgraph.HasNeighbors(s, step)
	})
}

// HasNovelsWith applies the HasEdge predicate on the "novels" edge with a given conditions (other predicates).
func HasNovelsWith(preds ...predicate.Novel) predicate.NovelClassify {
	return predicate.NovelClassify(func(s *sql.Selector) {
		step := sqlgraph.NewStep(
			sqlgraph.From(Table, FieldID),
			sqlgraph.To(NovelsInverseTable, FieldID),
			sqlgraph.Edge(sqlgraph.O2M, false, NovelsTable, NovelsColumn),
		)
		sqlgraph.HasNeighborsWith(s, step, func(s *sql.Selector) {
			for _, p := range preds {
				p(s)
			}
		})
	})
}

// And groups predicates with the AND operator between them.
func And(predicates ...predicate.NovelClassify) predicate.NovelClassify {
	return predicate.NovelClassify(func(s *sql.Selector) {
		s1 := s.Clone().SetP(nil)
		for _, p := range predicates {
			p(s1)
		}
		s.Where(s1.P())
	})
}

// Or groups predicates with the OR operator between them.
func Or(predicates ...predicate.NovelClassify) predicate.NovelClassify {
	return predicate.NovelClassify(func(s *sql.Selector) {
		s1 := s.Clone().SetP(nil)
		for i, p := range predicates {
			if i > 0 {
				s1.Or()
			}
			p(s1)
		}
		s.Where(s1.P())
	})
}

// Not applies the not operator on the given predicate.
func Not(p predicate.NovelClassify) predicate.NovelClassify {
	return predicate.NovelClassify(func(s *sql.Selector) {
		p(s.Not())
	})
}
